// Immediately Invoked Function Expression (IIFE) to create a private scope
(function () {
  "use strict"; //it forces to declarate the variables in the code

  // Variable declaration
  var isDrawing, lastDrawingPoint;
  var scratchContainer = document.getElementById("js-scratch-container");
  var scratchCanvas = document.getElementById("js-scratch-canvas");

  // Set size to scratch canvas before more variable declaration
  scratchCanvas.height = scratchContainer.clientHeight;
  scratchCanvas.width = scratchContainer.clientWidth;

  // Variable declaration
  var canvasWidth = scratchCanvas.width;
  var canvasHeight = scratchCanvas.height;
  var context = scratchCanvas.getContext("2d");
  var scratchCoverImage = new Image();
  var scratchBrushImage = new Image();

  // Image to scratch converted to base64 workaround because Same-Origin-Policy (will be blocked by CORS policy)
  scratchCoverImage.src =
    "data:image/png;base64,";

  // On load, draw the image in the canvas and show the scratch content
  scratchCoverImage.onload = function () {
    drawImageCentered(scratchCoverImage, context);
    // Show the scratch-content when the image is loaded.
    document.querySelectorAll(".scratch-content")[0].style.visibility = "visible";
  };

  // Image of the scratch brush converted to base64 (same as cover-image)
  scratchBrushImage.src =
    "data:image/png;base64,";

  // Event listeners for mouse and touch events
  scratchCanvas.addEventListener("mousedown", startDrawing, false);
  scratchCanvas.addEventListener("mousemove", moveDrawing, false);
  scratchCanvas.addEventListener("mouseup", stopDrawing, false);
  scratchCanvas.addEventListener("touchstart", startDrawing, false);
  scratchCanvas.addEventListener("touchmove", moveDrawing, false);
  scratchCanvas.addEventListener("touchend", stopDrawing, false);

  // Function to draw an image scaled and centered
  function drawImageCentered(image, context) {
    var canvas = context.canvas;
    var horizontalRatio = canvas.width / image.width;
    var verticalRatio = canvas.height / image.height;
    var ratio = Math.min(horizontalRatio, verticalRatio);
    var centerXShift = (canvas.width - image.width * ratio) / 2;
    var centerYShift = (canvas.height - image.height * ratio) / 2;
    context.clearRect(0, 0, canvas.width, canvas.height);
    context.drawImage(image, 0, 0, image.width, image.height, centerXShift, centerYShift, image.width * ratio, image.height * ratio);
  }

  // Function to calculate the distance between two points
  function calculateDistance(point1, point2) {
    return Math.sqrt(
      Math.pow(point2.x - point1.x, 2) + Math.pow(point2.y - point1.y, 2)
    );
  }

  // Function to calculate the angle between two points
  function calculateAngle(point1, point2) {
    return Math.atan2(point2.x - point1.x, point2.y - point1.y);
  }

  // Function to get filled-in pixels percentage
  function getFilledInPixelsPercentage(stride) {
    if (!stride || stride < 1) {
      stride = 1;
    }

    // Get pixel data of the entire canvas
    var pixels = context.getImageData(0, 0, canvasWidth, canvasHeight),
      pixelData = pixels.data,
      dataLength = pixelData.length,
      totalPixels = dataLength / stride,
      filledPixelsCount = 0;

    // Iterate over pixels with the specified stride
    for (var i = (filledPixelsCount = 0); i < dataLength; i += stride) {
      // Check if the red channel of the current pixel is 0 (transparent)
      if (parseInt(pixelData[i]) === 0) {
        filledPixelsCount++;
      }
    }

    // Calculate and return the percentage of filled-in pixels
    return Math.round((filledPixelsCount / totalPixels) * 100);
  }

  // Function to get mouse coordinates relative to the canvas
  function getMouseCoordinates(event, canvas) {
    var offsetX = 0,
      offsetY = 0,
      mouseX,
      mouseY;

    // Check if the canvas has an offsetParent (a parent with a position set)
    if (canvas.offsetParent !== undefined) {
      // Loop through the offsetParents to accumulate the offset values
      do {
        offsetX += canvas.offsetLeft;
        offsetY += canvas.offsetTop;
      } while ((canvas = canvas.offsetParent)); // Move to the next offsetParent
    }

    // Calculate the mouse coordinates relative to the canvas
    mouseX = (event.pageX || event.touches[0].clientX) - offsetX;
    mouseY = (event.pageY || event.touches[0].clientY) - offsetY;

    // Return an object containing the relative x and y coordinates
    return { x: mouseX, y: mouseY };
  }

  // Function to handle the percentage of the scratched area and clear the canvas depending on the draw percentage
  function handleScratchedAreaPercentage(filledPixelsPercentage) {
    filledPixelsPercentage = filledPixelsPercentage || 0;
    console.log(filledPixelsPercentage + "%"); // Log the percentage in the console for checking
    if (filledPixelsPercentage > 80) {
      // If more than 80% is scratched, remove the scratch canvas
      scratchCanvas.parentNode.removeChild(scratchCanvas);
    }
  }

  // Event handler for mouse down
  function startDrawing(event) {
    isDrawing = true;
    lastDrawingPoint = getMouseCoordinates(event, scratchCanvas);
  }

  // Event handler for mouse move
  function moveDrawing(event) {
    // Check if drawing is not in progress, and exit the function if true
    if (!isDrawing) {
      return;
    }

    // Prevent the default behavior of the mouse move event
    event.preventDefault();

    // Get the current mouse coordinates relative to the canvas
    var currentDrawingPoint = getMouseCoordinates(event, scratchCanvas),
      // Calculate the distance and angle between the last and current drawing points
      distance = calculateDistance(lastDrawingPoint, currentDrawingPoint),
      angle = calculateAngle(lastDrawingPoint, currentDrawingPoint),
      x,
      y;

    // Loop through the distance between the drawing points
    for (var i = 0; i < distance; i++) {
      // Calculate new coordinates based on the distance, angle, and offset
      x = lastDrawingPoint.x + Math.sin(angle) * i - 20;
      y = lastDrawingPoint.y + Math.cos(angle) * i - 20;

      // Set the global composite operation to "destination-out" (erase)
      context.globalCompositeOperation = "destination-out";

      // Draw the scratch brush image at the calculated coordinates
      context.drawImage(scratchBrushImage, x, y);
    }

    // Update the last drawing point to the current drawing point for the next iteration
    lastDrawingPoint = currentDrawingPoint;

    // Calculate and handle the percentage of filled-in pixels with a stride of 32
    handleScratchedAreaPercentage(getFilledInPixelsPercentage(32));
  }

  // Event handler for mouse up
  function stopDrawing() {
    isDrawing = false;
  }
})();
